concord.timestamps = {}
function concord.lostbal(event,bal) concord.timestamps[bal] = getEpoch() end registerAnonymousEventHandler("concord lost bal","concord.lostbal")
function concord.gotbal(event,bal) local t = getEpoch()-concord.timestamps[bal] t = math.floor(t*1000)/1000 concord.balancetime = t concord.timestamps[bal] = getEpoch() end

